con:config.script:
// this is used to check if to see if Liesl can purchase or not

//We need to fetch the username first

def username = context.expand('${Login#Request#//*:username}')
// display what username has been retrived
//log.info username

// placing a if statement to check if the purchase can be done
if(username != "Liesl"){
	log.info "Not a Privilaged Customer - NO PURCHASE"
	//TO jump to the assigned services
	testRunner.gotoStepByName("Logout")
}
else{
	log.info "Privilaged Customer - Processed to Purchase"
}

con:configuration.scriptText:
// I will write a groovy script to capture the response and fetch the sessionID
// Store it in a test case/ test step level

// creating the variable 'response'
// the variable will fetch the response and store it in the variable

//Importing the library to talk to the xml file
import com.eviware.soapui.support.XmlHolder

//create a variable to fetch the response of login
def response = context.testCase.getTestStepByName('Login').getPropertyValue("Response")

//creating a holder to remove the tags and to fetch the data from the response
def holder = new XmlHolder(response)

def session = holder.getNodeValue("//*:sessionId")
// to see the value stored is session
log.info session
// Storing the value into the test suite
context.testCase.testSuite.setPropertyValue("sessionId", session)

con:config.script:
import com.eviware.soapui.model.testsuite.TestRunner.Status
// import the library

// we want a test runner to interacte with another test case in another test suite with our current test case
